import React, { Component } from 'react'
import ToDolist from "./index.css"
import Header from "./components/Header"
import List from "./components/List"
export default class ToDoList extends Component {
  state = { toDoList: [111, 222, 333] }
  render() {
    return (
      <div>
        <Header ref={c => this.ref1 = c} onKeyDown={this.onKeyDown} />
        {/* <button onClick={this.changeWeather}>添加</button> */}
        <List toDoListData={this.state.toDoList} />

      </div>
    )
  }
  //自定义方法————要用赋值语句的形式+箭头函数
  // changeWeather = () => {  // 箭头函数没有自己的this，他会往外找，这里函数内部的this相当于weather实例对象的this
  //   let toDoList = this.state.toDoList
  //   let inputValue = this.ref1.ref2.value || ''
  //   console.log(this);
  //   this.setState({ toDoList: [...toDoList, inputValue] })
  // }
  //自定义方法————要用赋值语句的形式+箭头函数
  onKeyDown = (e) => {
    if (e.code == 'enter') {
      let toDoList = this.state.toDoList
      let inputValue = this.ref1.ref2.value || ''

      this.setState({ toDoList: [...toDoList, inputValue] })
    }

    console.log(e.code);
  }
}
