import React, { Component } from 'react'
import ToDolist from "./index.css"
export default class ToDoList extends Component {
  state = { isHot: false, wind: '微风' }
  render() {
    return (
      <div>
        <input type="text" placeholder='请输入你的待办事项' />
        <button onClick={this.changeWeather}>添加</button>
        <ul>
          {
            this.state.map((item, index) => {
              return <li key={index}>{item}</li>
            })
          }
        </ul>
      </div>
    )
  }
  //自定义方法————要用赋值语句的形式+箭头函数
  changeWeather = (e) => {  // 箭头函数没有自己的this，他会往外找，这里函数内部的this相当于weather实例对象的this
    const newState = this.state.push(e.target.value)
    console.log(this);
    this.setState(...this.state)
  }
}
