import { Button } from 'antd';
// import { Icon } from '@ant-design/compatible';
// const { Option } = Select;
export default class Count1 extends Component {
  state = { num: 0 }
  handleChange = (e) => {
    console.log('this.refs.list0.value', this.refs.list0.value);
    console.log(e);
  }
  render() {

    const { num } = this.state
    return (

      <div>
        <h1>当前求和为：{num}</h1>  <br />
        <select defaultValue="1" style={{ width: 30, height: 35 }} ref="list0">
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
        </select>
        <Button icon="+" onClick={this.handleChange} ref="list1" />
        <Button icon="-" onClick={this.handleChange} ref="list2" />
        <Button >increment if odd</Button>
        <Button >increment async</Button>
      </div>
    )
  }
}
